sysauto_script.script:
function findAwaitingUserInfoDelayed(state, number) {
    var incidentGA = new GlideAggregate('incident'); // create new aggregate
    incidentGA.addQuery('state', 3); // condition = state is on hold
    incidentGA.addQuery('hold_reason', 1); // hold reason = Awaiting Caller
    incidentGA.addQuery('opened_at', '>=', 'javascript:gs.daysAgo(3)'); // checking if opened more than 3 days ago
    incidentGA.addAggregate('COUNT', 'sys_mod_count'); // count all records
    incidentGA.query();

    if (incidentGA.next()) {
        gs.info('COUNT: ' + incidentGA.getAggregate('COUNT', 'sys_mod_count')); // create log with the result from counting
    }
}
